    private void txtCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtCalcularActionPerformed
    double lado;
    double  area;
    double perimetro;
    double  altura;
    double  base;
    
    lado=Double.parseDouble(txtLado.getText());
    perimetro=lado*3;
    base=lado/2;
    altura=Math.sqrt(Math.pow(lado,2)-Math.pow(base,2));
    area=(lado*altura)/2;
    
    txtLado.setText(String.valueOf(lado));
    txtBase.setText(String.valueOf(base));
    txtAltura.setText(String.valueOf(altura));
    txtArea.setText(String.valueOf(area));
    txtPerimetro.setText(String.valueOf(perimetro));
    
    
    // TODO add your handling code here:
    }//GEN-LAST:event_txtCalcularActionPerformed

